import React, { useEffect, useState } from 'react'
import Table from 'react-bootstrap/Table';
import './potable.css'
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';
import CreateIcon from '@mui/icons-material/Create';
import DeleteIcon from '@mui/icons-material/Delete';
import Addpo from './Addpo';
import { Link } from 'react-router-dom';

const Potable = () => {

const[podata, setpodata]=useState([]);
console.log(podata)


  const getdata = async(e)=>{
  
const res = await fetch("http://localhost:8003/getdata", {method :"GET",
headers:{"Content-Type":"application/json"},
});
const data = await res.json();
console.log(data);

if(res.status === 404|| !data){
    console.log("error")
}else{

  setpodata(data);
    console.log("get data")
}

}

useEffect(()=>{
  getdata();
},[]);

const deletepo = async(id)=>{

  const res2 = await fetch(`http://localhost:8003/delete/${id}`,{
    method: "DELETE",
            headers: { "Content-Type": "application/json" },
            
  }
  )

  const deletepo = await res2.json()
console.log(deletepo)
if(res2.status === 404|| !deletepo){
  console.log("error")
}else{
  console.log("deleted")
  getdata();
}


};

  return (
    <>
    <Addpo/>
    <div className='mt-5'>
    <div className='container'>
    <Table >
      <thead>
        <tr className='table-light'>
          <th>Order#</th>
          <th>Order Date</th>
          <th>PO #</th>
          <th>Customer</th>
          <th>Item ID</th>
          <th>Status</th>
          <th>Created By</th>
          <th></th>
        </tr>
      </thead>
      <tbody>

        {
          podata.map((ele,order)=>{
            return <>

<tr>
          <td>{ele.order}</td>
          <td>{ele.date}</td>
          <td>{ele.po}</td>
          <td>{ele.name}</td>
          <td>{ele.id}</td>
          <td>{ele.status}</td>
          <td>{ele.createdby}</td>
          <td className='d-flex justify-content-between '>

            <Link to ={`view/${ele._id}`}>
          <RemoveRedEyeIcon className='icons'/>
          </Link>
          <Link to ={`edit/${ele._id}`}>
          <CreateIcon className='icons'/>
          </Link>


          <DeleteIcon className='icons' onClick ={()=>deletepo(ele._id)}/>
            
          </td>
        </tr>
            </>
          })
        }
        
        
      </tbody>
    </Table>
    </div>
    </div>

    
    </>
  )
}

export default Potable
